contracts/interfaces/IGovernanceToken.so.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.13;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "./IService.sol";

interface IGovernanceToken is IERC20Upgradeable {
    struct TokenInfo {
        string name;
        string symbol;
        uint256 cap;
    }

    function initialize(address pool_, TokenInfo memory info) external;

    function mint(address to, uint256 amount) external;

    function burn(address from, uint256 amount) external;

    function lock(
        address account,
        uint256 amount,
        uint256 deadline
    ) external;

    function cap() external view returns (uint256);

    function unlockedBalanceOf(address account) external view returns (uint256);

    function pool() external view returns (address);

    function service() external view returns (IService);
}

contracts/interfaces/ITGE.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.13;

interface ITGE {
    struct TGEInfo {
        string metadataURI;
        uint256 price;
        uint256 hardcap;
        uint256 softcap;
        uint256 minPurchase;
        uint256 maxPurchase;
        uint256 lockupPercent;
        uint256 lockupDuration;
        uint256 lockupTVL;
        uint256 duration;
        address[] userWhitelist;
        address unitOfAccount;
    }

    function initialize(
        address owner_,
        address token_,
        TGEInfo memory info
    ) external;

    function redeem() external;

    function maxPurchaseOf(address account) external view returns (uint256);

    enum State {
        Active,
        Failed,
        Successful
    }

    function state() external view returns (State);
}

